            aria-label="Next testimonial"
            data-testid="button-next-testimonial"
          >
            <ChevronRight className="w-5 h-5" />
          </Button>

          {/* Testimonial Display */}
          <div className="px-12 py-4">
            <AnimatePresence mode="wait">
              <motion.div
                key={currentIndex}
                initial={{ opacity: 0, x: 100 }}
                animate={{ opacity: 1, x: 0 }}
                exit={{ opacity: 0, x: -100 }}
                transition={{ duration: 0.5, ease: "easeInOut" }}
              >
                <Card className="bg-gradient-to-br from-gray-50 to-gray-100 border border-gray-200 shadow-lg">
                  <CardContent className="p-8 md:p-12">
                    <div className="text-center">
                      {/* Rating Stars */}
                      <div className="flex justify-center items-center mb-6">
                        <div className="flex text-secondary">
                          {[...Array(TESTIMONIALS[currentIndex].rating)].map(
                            (_, i) => (
                              <Star
                                key={i}
                                className="w-5 h-5 fill-current"
                                aria-hidden="true"
                              />
                            ),
                          )}
                        </div>
                      </div>

                      {/* Testimonial Text */}
                      <blockquote className="text-lg md:text-xl text-foreground mb-8 italic leading-relaxed">
                        "{TESTIMONIALS[currentIndex].comment}"
                      </blockquote>

                      {/* Client Info */}
                      <div className="flex items-center justify-center">
                        <div className="w-14 h-14 bg-primary rounded-full flex items-center justify-center text-white font-semibold mr-4">
                          <span className="text-lg">
                            {TESTIMONIALS[currentIndex].initials}
                          </span>
                        </div>
                        <div className="text-left">
                          <div className="font-bold text-foreground text-lg">
                            {TESTIMONIALS[currentIndex].name}
                          </div>
                          <div className="text-muted-foreground">
                            {TESTIMONIALS[currentIndex].location}
                          </div>
                        </div>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              </motion.div>
            </AnimatePresence>
          </div>

          {/* Pagination Dots */}
          <div className="flex justify-center mt-8 space-x-2">
            {TESTIMONIALS.map((_, index) => (
              <button
                key={index}
